// Código para nó “Code” no n8n
// items é um array onde cada item.json.id contém um ID
const result = {};

// Para cada item, cria uma propriedade audio{índice+1} = id
items.forEach((item, index) => {
  result[`audio${index + 1}`] = item.json.id;
});

// Retorna um único item com todas as chaves
return [
  {
    json: result
  }
];

// 1) Pega o texto de entrada
const text = $input.first().json.data || '';

// 2) Separa em sentenças
const sentences = text.match(/[^\.!\?]+[\.!\?]+["']?|[^\.!\?]+$/g) || [];
const totalSentences = sentences.length;

// 3) Define número de pedaços e calcula tamanho base + resto
const numChunks = 60;
const baseSize = Math.floor(totalSentences / numChunks);
const remainder = totalSentences % numChunks;

// 4) Monta os chunks com distribuição uniforme sem cortar frases
const parts = [];
let idx = 0;
for (let i = 0; i < numChunks; i++) {
  const size = baseSize + (i < remainder ? 1 : 0);
  const chunkSentences = sentences.slice(idx, idx + size);
  const chunk = chunkSentences.join(' ').trim();
  parts.push(chunk);
  idx += size;
}

// 5) Garante 60 pedaços (por segurança)
while (parts.length < numChunks) {
  parts.push('');
}

// 6) Monta o objeto de saída
const output = {
  TEXTOS: parts.map(segment => ({ texto: segment }))
};

// 7) Retorna o resultado
return [{ json: output }];
